import styled from 'styled-components'

export const StyledCenteredButton = styled.div`
box-sizing: border-box;
margin: 20px auto auto auto;
padding-top: 7px;
border: 4px solid #333;
height: 50px;
width: 15%;
min-width: 260px;
max-width: 280px;
border-radius: 20px;
color: #999;
background: #000;
font-size: 24px;
cursor: pointer;
`;
